import { takeEvery } from '@redux-saga/core/effects';

import {
  firestore,
  convertCollectionsSnapshotToMap,
} from '../../firebase/firebase.utils';

import {
  fecthCollectionsSuccess,
  fecthCollectionsFailure,
} from './shop.actions';

import ShopActionTypes from './shop.types';

export function* fetchCollectionsAsync() {
  yield console.log('FIREE');

  const collectionRef = firestore.collection('collections');
  const snapshot = yield collectionRef.get() //comes as a promise

  // collectionRef
  //   .get()
  //   .then(async (snapshot) => {
  //     const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
  //     dispatch(fecthCollectionsSuccess(collectionsMap));
  //   })
  //   .catch((error) => dispatch(fecthCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionTypes.FECTH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
